$(document).ready(function(){	$('#tabs .tab').hide();	$('#tabs .tab:first').show();	$('#mainMenu ul li:first').addClass('active');	$('#mainMenu ul li a').click(function(){		$('#mainMenu ul li').removeClass('active');		$(this).parent().addClass('active');		var currentTab = $(this).attr('href');		$('#tabs .tab').hide();		$(currentTab).show();		return false;	});		$('.soniclean, .sitelogo').click(function() {		$('#mainMenu ul li').removeClass('active');		var currentTab = $(this).attr('href');		$('#tabs .tab').hide();		$(currentTab).show();	});		$("#footer .thisYear").text( (new Date).getFullYear() );});